<?php// Lấy danh sách các URL ảnh đã được lưu trong phần cài đặt$images = get_option('swiper_slider_images', []);// Nếu có ảnh, hiển thị sliderif (!empty($images)) : ?><div id="slider_banner_home">    <div class="swiper-container slider1">        <div class="swiper-wrapper">            <?php foreach ($images as $image_url) : ?>                <div class="swiper-slide">                    <img src="<?php echo esc_url($image_url); ?>" alt="Slide" />                </div>            <?php endforeach; ?>        </div>        <!-- Pagination -->        <div class="swiper-pagination swiper-pagination1"></div>        <!-- If we need navigation buttons -->        <div class="swiper-button-next swiper-button-next-1"></div>        <div class="swiper-button-prev swiper-button-prev-1"></div>    </div></div><?php else : ?>    <p><?php esc_html_e('No images available for the slider.', 'swiper-slider'); ?></p><?php endif; ?>